    public static String drawBar(int current, int max, int width) {
        if (max <= 0) max = 1; 
        double percent = (double) current / max;
        int filled = (int) (percent * width);
        if (filled < 0) filled = 0;
        if (filled > width) filled = width;
        
        StringBuilder sb = new StringBuilder("[");
        for (int i = 0; i < width; i++) {
            if (i < filled) sb.append("â–ˆ");
            else sb.append("â–‘");
        }
        sb.append("]");
        return sb.toString();
    }

    public static String padRight(String s, int n) {
        return String.format("%-" + n + "s", s);
    }

    public static void printMenuHeader(String title) {
        System.out.println("\n.---------------------------------.");
        System.out.println("| " + padCenter(title, 31) + " |");
        System.out.println("'---------------------------------'");
    }

    public static String padCenter(String s, int width) {
        if (s.length() >= width) return s;
        int padding = (width - s.length()) / 2;
        return " ".repeat(padding) + s + " ".repeat(width - padding - s.length());
    }

