    public static void printBox(String text) {
        int len = text.length();
        String border = "═".repeat(len + 4);
        System.out.println("╔" + border + "╗");
        System.out.println("║  " + text + "  ║");
        System.out.println("╚" + border + "╝");
    }

    public static void drawBattleUI(Enemy e, Brainrot p) {
        String enemyName = padRight(e.getName(), 20);
        String playerName = padRight(p.getName(), 20);
        
        System.out.println("\n");
        System.out.println("   ╔════════════════════════════════════╗");
        System.out.println("   ║ ENEMY: " + enemyName + " ║");
        System.out.println("   ║    HP: " + drawBar(e.getCurrentHp(), e.getMaxHp(), 15) + " " + e.getCurrentHp() + " ║");
        System.out.println("   ║    MP: " + drawBar(e.getCurrentMana(), e.getMaxMana(), 10) + " " + e.getCurrentMana() + " ║");
        System.out.println("   ╚════════════════════════════════════╝");
        System.out.println("                 ⚡ VS ⚡");
        System.out.println("   ╔════════════════════════════════════╗");
        System.out.println("   ║  YOU:   " + playerName + " ║");
        System.out.println("   ║    HP: " + drawBar(p.getCurrentHp(), p.getMaxHp(), 15) + " " + p.getCurrentHp() + " ║");
        System.out.println("   ║    MP: " + drawBar(p.getCurrentMana(), p.getMaxMana(), 10) + " " + p.getCurrentMana() + " ║");
        System.out.println("   ╚════════════════════════════════════╝");
        System.out.println("\n");
    }

    public static String drawBar(int current, int max, int width) {
        if (max <= 0) max = 1; 
        double percent = (double) current / max;
        int filled = (int) (percent * width);
        if (filled < 0) filled = 0;
        if (filled > width) filled = width;
        
        StringBuilder sb = new StringBuilder("[");
        for (int i = 0; i < width; i++) {
            if (i < filled) sb.append("█");
            else sb.append("░");
        }
        sb.append("]");
        return sb.toString();
    }

    public static String padRight(String s, int n) {
        return String.format("%-" + n + "s", s);
    }

    public static void printMenuHeader(String title) {
        System.out.println("\n.---------------------------------.");
        System.out.println("| " + padCenter(title, 31) + " |");
        System.out.println("'---------------------------------'");
    }

    public static String padCenter(String s, int width) {
        if (s.length() >= width) return s;
        int padding = (width - s.length()) / 2;
        return " ".repeat(padding) + s + " ".repeat(width - padding - s.length());
    }

